      setIsError(false);
      setIsLoading(true);
      await axios(url,{ withCredentials: true }).then((response) => {        
        const data = response.data;                
        setData(data);
        setAuth(true);
        setIsLoading(false);
        setIsError(false);
      }).catch((error) => {
        console.log(JSON.stringify(error));
        setIsLoading(false);
        if (error.response && error.response.status === 401) {
          setAuth(false);
        } else {
          setIsError(true);
        }
      });
    };

    fetchData();
  }, [url]);

  if (isLoading) return(<div>Loading ...</div>);
  if (isError) return(<div>Something went wrong ...</div>);
  if (!auth) return(<Redirect to={{pathname: '/login'}} />);


  return (
    <Card>
      <CardHeader color="success">
        <h4 className={classes.cardTitleWhite}>Pushes waiting for Authorization</h4>
        <p className={classes.cardCategoryWhite}>          
        </p>
      </CardHeader>
      <CardBody>
        {
          isLoading ? (
            <div>Loading ...</div>
          ) : (
            <TableContainer component={Paper}>
              <Table className={classes.table} aria-label="simple table">
                <TableHead>
                  <TableRow>
                    <TableCell>Actions</TableCell>
                    <TableCell align="right">Time</TableCell>
                    <TableCell align="right">Repo</TableCell>
                    <TableCell align="right">Branch</TableCell>
                    <TableCell align="right">Commit</TableCell>
                    <TableCell align="right">Git Author</TableCell>
                    <TableCell align="right">Internal Id</TableCell>
                    <TableCell align="right">Message</TableCell>
                  </TableRow>
                </TableHead>
                <TableBody>
                  {data.map((row) => (
                    <TableRow key={row.id}>
                      <TableCell component="th" scope="row">
                        <Button variant="contained" color="primary" onClick={() => openPush(row.id)}>
                          Open
                        </Button>
                      </TableCell>
                      <TableCell align="right" >
                        {moment(row.timestamp).format("yyyy-MM-DD HH:mm")}
                      </TableCell>
                      <TableCell align="right">{row.repo}</TableCell>
                      <TableCell align="right">{row.branch.replace('refs/heads/', '')}</TableCell>
                      <TableCell align="right">{row.commitFrom.substring(0,7)} - {row.commitTo.substring(0,7)}</TableCell>
                      <TableCell align="right">{row.gitAuthor}</TableCell>
                      <TableCell align="right">{row.internalAuthor}</TableCell>
                      <TableCell align="right">{row.message}</TableCell>
                    </TableRow>
                  ))}
                </TableBody>
              </Table>
            </TableContainer>
          )
        },
      </CardBody>
    </Card>
  );
}

import React, { useState, useEffect } from 'react';
import GridItem from "ui/components/Grid/GridItem.js";
import GridContainer from "ui/components/Grid/GridContainer.js";
import { makeStyles } from "@material-ui/core/styles";
import Card from "ui/components/Card/Card.js";
import CardIcon from "ui/components/Card/CardIcon.js";
import CardBody from "ui/components/Card/CardBody.js";
import CardHeader from "ui/components/Card/CardHeader.js";
import CardFooter from "ui/components/Card/CardFooter.js";
import Button from "ui/components/CustomButtons/Button.js";
import axios from 'axios';
import moment from "moment";
import Paper from '@material-ui/core/Paper';
import Icon from "@material-ui/core/Icon";
import { Redirect } from "react-router-dom";
import Diff from "./components/Diff";
import styles from "ui/assets/jss/material-dashboard-react/views/dashboardStyle.js";

const useStyles = makeStyles(styles);

export default function Dashboard(props) {  
  const id = props.match.params.id;
  const classes = useStyles();
  const [data, setData] = useState([]);  
  const [auth, setAuth] = useState(true);
  const url = `http://localhost:8080/api/v1/push/${id}`;
  const [isLoading, setIsLoading] = useState(true);
  const [isError, setIsError] = useState(false);

  useEffect(() => {
    const fetchData = async () => {
      setIsError(false);
      setIsLoading(true);
      await axios(url,{ withCredentials: true }).then((response) => {        
        const data = response.data;
        data.diff = data.steps.find(x => x.stepName === 'diff');
        console.log(data.diff);
        console.log(data)
        setData(data);
        setAuth(true);
        setIsLoading(false);
        setIsError(false);
      }).catch((error) => {
        console.log(JSON.stringify(error));
        setIsLoading(false);
        if (error.response && error.response.status === 401) {
          setAuth(false);
        } else {
          setIsError(true);
        }
      });
    };

    fetchData();
  }, [url]);

  if (isLoading) return(<div>Loading ...</div>);
  if (isError) return(<div>Something went wrong ...</div>);
  if (!auth) return(<Redirect to={{pathname: '/login'}} />);

  return (    
    <GridContainer>        
      <GridItem xs={12} sm={12} md={12}>
        <Card>
          <CardHeader color="success" stats icon>
            <CardIcon color="success">
              <Icon>content_copy</Icon>
            </CardIcon>          
          </CardHeader>
          <CardBody>
            <GridContainer>        

            <GridItem xs={2} sm={2} md={2}>
                <h3>Commit</h3>            
                <p>{moment(data.timestamp).toString()}</p>
              </GridItem>              
              <GridItem xs={3} sm={3} md={3}>
                <h3>Commit From</h3>            
                <p>{data.commitFrom}</p>
              </GridItem>
              <GridItem xs={3} sm={3} md={3}>
                <h3>Commit From</h3>            
                <p>{data.commitTo}</p>
              </GridItem>              
              <GridItem xs={2} sm={2} md={2}>
                <h3>Repo</h3>            
                <p>{data.repo}</p>
              </GridItem>
              <GridItem xs={2} sm={2} md={2}>
                <h3>Branch</h3>            
                <p>{data.branch.replace('refs/heads/', '')}</p>
              </GridItem>
              <GridItem xs={2} sm={2} md={2}>
                <h3>Author</h3>            
                <p>{data.author} pGrovesy</p>
              </GridItem>
              <GridItem xs={10} sm={10} md={10}>
                <h3>Message</h3>
                <p>{data.commitMessage} Temporary placeholder message</p>
              </GridItem>              
            </GridContainer>        
          </CardBody>
        </Card>
      </GridItem>

      <GridItem xs={12} sm={12} md={12}>
        <Card>
          <CardHeader></CardHeader>
          <CardBody>
            <Diff diff={data.diff.content}/>
          </CardBody>
          <CardFooter>
            <Button>Rject</Button>
            <Button>Approve</Button>          
          </CardFooter>
        </Card>

      </GridItem>                
    </GridContainer>    
  );
}

import * as Diff2Html from 'diff2html';
import ReactHtmlParser from 'react-html-parser'; 

export default function Diff(props) {
  const {diff} = props;  
  let outputHtml = Diff2Html.html(diff, { drawFileList: true, matching: 'lines' });  

  return (
    ReactHtmlParser (outputHtml)
  );
}

