const config = require('../config');
let sink = null

console.log(config.getSink())
if (config.getSink() == 'fs') {
  sink = require('./fsSink');
  console.log(sink)
}

const getPushes = () => {
  return sink.getPushes();
}

const getPush = (id) => {
  return sink.getPush(id);
}

const writeAudit = (result) => {
  return sink.writeAudit(result);
}

const authorise = (id) => {
  console.log(`data authorised exec`)
  return sink.authorise(id);
}



module.exports.authorise = authorise;
module.exports.getPushes = getPushes;
module.exports.writeAudit = writeAudit;
module.exports.getPush = getPush;
