const Step = require('proxy/actions').Step;

const exec = (req, action) => {
  const step = new Step('authBlock');
  step.setAsyncBlock(`Your push request is waiting authorisation, tracking id http://localhost:8080/requests/${action.timestamp}`);
  action.addStep(step);
  return action;
};

exec.displayName = 'blockForAuth.exec';
exports.exec = exec;
